    def calcular(self):
        import math
        arriba = (float(self.vano1.text()) ** 4) + \
            (float(self.vano2.text()) ** 4) + (float(self.vano3.text()) ** 4)
        abajo = ((float(self.vano1.text()) ** 2) + (float(self.vano2.text()) ** 2) + (float(self.vano3.text()) ** 2)) + \
            ((float(self.altura1.text()) ** 2) + (float(self.altura2.text()) ** 2) +
             (float(self.altura3.text()) ** 2) + (float(self.altura4.text()) ** 2))

        raiz1 = math.sqrt(abajo)

        result1 = arriba / raiz1
        resultTotal = math.sqrt(result1)

        self.resultado.setText(str(round(resultTotal, 2)))

        f = open('data.txt', 'a')
        f.write(str(round(resultTotal, 2)))
        f.close()




